#!/bin/bash

if [ "$#" -ne 4 ]; then
    echo "Usage: ./deploy.sh <commit_message> <minor_version> <patch_version> <tag>"
    exit 1
fi

COMMIT_MESSAGE="$1"
MINOR_VERSION="$2"
PATCH_VERSION="$3"
TAG="$4"

git add . && \
git commit -m "$COMMIT_MESSAGE" && \
npm version "1.$MINOR_VERSION.$PATCH_VERSION" && \
npm publish --tag $TAG 

