const isKitchenPackage = term => term && term.length > 0 && term.includes('Kitchen Package');

function checkProductContent(itemData) {
  return {
    hasImage1: Boolean(itemData.image1WithHeightAndWidth),
    hasImage2: Boolean(itemData.image2WithHeightAndWidth),
    hasImage3: Boolean(itemData.image3WithHeightAndWidth),
    hasFirstRichTextContent: Boolean(itemData.richTextContent),
    hasSecondRichTextContent: Boolean(itemData.secondRichTextContent),
    hasThirdRichTextContent: Boolean(itemData.thirdRichTextContent),
    hasMainVideo: Boolean(itemData.vimeoLink) || Boolean(itemData.mainVideo),
    hasGallery: Boolean(itemData.gallery),
  };
}
function isWarrantyProduct(slug) {
  return slug && slug.includes('years');
}
module.exports = {
  isKitchenPackage,
  checkProductContent,
  isWarrantyProduct,
};
