import { Storage } from './storage.types';
/**
 * Used for local storage of data.
 *
 * Use the methods in [`Storage`](storage.Storage.html) to manage `local` data.
 * @readonly
 * @servicePath wix-storage-frontend.Storage
 */
export function local(): Promise<Storage>;
/**
 * Used for memory storage of data.
 *
 * Use the methods in [`Storage`](storage.Storage.html) to manage `memory` data.
 * @readonly
 * @servicePath wix-storage-frontend.Storage
 */
export function memory(): Promise<Storage>;
/**
 * Used for session storage of data.
 *
 * Use the methods in [`Storage`](storage.Storage.html) to manage `session` data.
 * @readonly
 * @servicePath wix-storage-frontend.Storage
 */
export function session(): Promise<Storage>;
